import { Component, OnInit } from '@angular/core';
import User from 'src/app/models/user';
import { Service } from 'src/app/services/service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent implements OnInit {
  highestTotalUser!: User;
  itemsCount!: number;
  usersCount!: number;
  randomQuote!: string;

  constructor(private service: Service) {}

  loadHighestTotaluser() {
    this.service.loadHighestTotalUser().subscribe((resp: any) => {
      console.log(resp)
      this.highestTotalUser = resp;
    });
  }

  loadItemsCount() {
    this.service.loadItemsCount().subscribe((resp: any) => {
      this.itemsCount = resp;
    });
  }

  loadUsersCount() {
    this.service.loadUsersCount().subscribe((resp: any) => {
      this.usersCount = resp;
    });
  }

  loadRandomQuote() {
    this.service.loadRandomQuote().subscribe((resp: any) => {
      this.randomQuote = resp;
    });
  }

  ngOnInit(): void {
    this.loadHighestTotaluser();
    this.loadItemsCount();
    this.loadUsersCount();
    this.loadRandomQuote();
  }
}
<app-header></app-header>
<div class="h-full w-full flex flex-row flex-wrap justify-center mt-24">
  <div
    class="p-6 w-3/12 h-2/5 flex flex-col items-center justify-center text-gray-700 bg-gray-200 hover:bg-gray-300 hover:animate-pulse rounded-md"
  >
    <p class="text-2xl mb-5">There are currently</p>
    <p class="text-4xl mb-5">
      <span class="font-bold">{{ itemsCount }}</span> items
    </p>
    <p class="text-2xl">being paid off</p>
  </div>
  <div
    class="p-6 w-3/12 h-2/5 mx-14 flex flex-col items-center justify-center text-gray-700 bg-gray-200 hover:bg-gray-300 hover:animate-pulse rounded-md"
  >
    <p class="text-4xl font-bold uppercase mb-6">
      {{ highestTotalUser.firstName }}
    </p>
    <p class="text-2xl text-center">currently has most contributions</p>
  </div>
  <div
    class="p-6 w-3/12 h-2/5 flex flex-col items-center justify-center text-gray-700 bg-gray-200 hover:bg-gray-300 hover:animate-pulse rounded-md"
  >
    <p class="text-2xl mb-5">There are</p>
    <p class="text-4xl font-bold mb-5">{{ loadUsersCount }}</p>
    <p class="text-2xl">Registered users</p>
  </div>
</div>
