import { Component, OnInit } from '@angular/core';
import Item from 'src/app/models/item';
import { Service } from 'src/app/services/service';
import { ChangeDetectionStrategy, ViewEncapsulation } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-items',
  templateUrl: './items.component.html',
  styleUrls: ['./items.component.css'],
})
export class ItemsComponent implements OnInit {
  public items: Item[] = [];

  constructor(public service: Service, private fb: FormBuilder) {}

  itemForm = this.fb.group({
    name: ['', [Validators.required]],
    description: ['', [Validators.required]],
    price: ['', [Validators.required]],
    isShared: [false],
  });

  onSubmit(): void {
    this.handleAddItem();
  }

  handleAddItem() {
    this.logValidationErrors();

    if (this.itemForm.valid) {
      const name = !!this.itemForm.value.name ? this.itemForm.value.name! : '';
      const description = !!this.itemForm.value.description
        ? this.itemForm.value.description!
        : '';
      const price = !!this.itemForm.value.price
        ? +this.itemForm.value.price!
        : 0;
      const isShared = !!this.itemForm.value.isShared
        ? this.itemForm.value.isShared!
        : false;
      const isPayedOff = false;
      const iconPath = 'default';
      const payedById = '00000000-0000-0000-0000-000000000001';

      let item = new Item("", name, description, cre)

      this.service.addItem(item).subscribe({
        next: () => {
          this.itemForm.reset();
          this.ngOnInit;
        },
      });
    }
  }

  logValidationErrors(group: FormGroup = this.itemForm): void {
    // get all root keys
    Object.keys(group.controls).forEach((key: string) => {
      const abstractControl = group.get(key);
      if (abstractControl instanceof FormGroup) {
        this.logValidationErrors(abstractControl);
      } else {
        // @ts-ignore
        this.formErrors[key] = '';
        if (
          abstractControl &&
          !abstractControl.valid &&
          (abstractControl.touched || abstractControl.dirty)
        ) {
          // @ts-ignore
          const messages = this.validationMessages[key];
          for (const errorKey in abstractControl.errors) {
            if (errorKey) {
              // @ts-ignore
              this.formErrors[key] += messages[errorKey] + ' ';
            }
          }
        }
      }
    });
  }

  validationMessages = {
    name: {
      length: 'That name is too short',
    },
    description: {
      length: 'The provided description is too short',
    },
    price: {
      length: "Price can't be empty",
      nan: "That's not a correct price format",
    },
  };
  formErrors = {
    name: '',
    description: '',
    price: '',
  };

  ngOnInit(): void {
    this.service.loadItems().subscribe({
      next: (res) => {
        console.log(res);
        res.forEach((c: any, index: number) => {
          if (index !== 0) {
            console.log(c);
            this.items.push(c);
          }
        });
      },
    });
  }
}
<div class="w-full h-full flex flex-col items-center">
  <div class="w-full flex flex-col items-center text-gray-700">
    <h1 class="text-5xl uppercase font-semibold mb-2">Items</h1>
  </div>
  <div
    class="w-full mt-10 pb-10 flex justify-center flex-wrap gap-10 overflow-auto"
  >
    <app-item-card *ngFor="let item of items" [item]="item"></app-item-card>
  </div>
</div>
<button
  title="Add item"
  class="fixed z-10 top-20 right-8 uppercase bg-gray-600 rounded-lg py-2 px-5 flex justify-center items-center text-white text-2xl hover:bg-gray-700"
  data-modal-target="default"
  data-modal-toggle="addModal"
  type="button"
>
  Add item
</button>

<!-- Main modal -->
<div
  id="addModal"
  tabindex="-1"
  aria-hidden="true"
  class="fixed top-0 left-0 right-0 z-50 hidden w-full p-4 overflow-x-hidden overflow-y-auto md:inset-0 h-modal md:h-full"
>
